import React, { Component } from 'react';
import Countries from './Countries.json';

class Form extends Component {
    constructor(){
    super()
    this.state = {
        name: "",
        lastName: "",
        date: "1901-01-01",
        countries: ""
      }
      this.changeHandle = this.changeHandle.bind(this)
    }

    changeHandle(event){
        this.setState({
        [event.target.name]: event.target.value
    })

    }
    render() {
        console.log(this.state)
        return (
            <div>
                <h1>Mission to Mars Registration Form</h1>
                <div>
                    <label htmlFor="name">First Name</label>
                    <input onChange={this.changeHandle} type="text" name="name" value={this.name}></input>
                    <br/>
                    <br/>
                    <label htmlFor="lastName">Last Name</label>
                    <input onChange={this.changeHandle} type="text" name="lastName" value={this.lastName}></input>
                   < hr/>
                    <label for="start">Start date:</label>
                    <input onChange={this.changeHandle} type="date" name="date" value="2018-07-22" min="1901-01-01" max="2018-12-31"/>
                    < br/>
                    <br/>
                    <br/>
                    <br/>
                    <p>Select your Country of Origin</p>
                    < select id = "country" onchange = "show(this)" >
                        <option value = "" > Country of Origin </option> 
                    </select>

                </div>
            </div>

        )
    }
}

export default Form;
